import logo from './logo.svg';
import './App.css';

import React, { useState } from 'react';
import { gql, useQuery, useMutation } from '@apollo/client';
import './App.css';

// GraphQL Query untuk mengambil pesan
const GET_MESSAGES = gql`
  query GetMessages {
    getMessages
  }
`;

// GraphQL Mutation untuk menambah pesan
const ADD_MESSAGE = gql`
  mutation AddMessage($text: String!) {
    addMessage(text: $text)
  }
`;

function MessagesList() {
  const { loading, error, data, refetch } = useQuery(GET_MESSAGES);

  if (loading) return <p>Loading messages...</p>;
  if (error) return <p>Error fetching messages: {error.message}</p>;

  return (
    <div>
      <h2>Messages</h2>
      {data && data.getMessages && data.getMessages.length > 0 ? (
        <ul>
          {data.getMessages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      ) : (
        <p>No messages yet.</p>
      )}
      <button onClick={() => refetch()}>Refresh Messages</button>
    </div>
  );
}

function AddMessageForm() {
  const [messageText, setMessageText] = useState('');
  const [addMessage, { data, loading, error }] = useMutation(ADD_MESSAGE, {
    // Opsi untuk refetch query GET_MESSAGES setelah mutasi berhasil
    // agar daftar pesan otomatis terupdate
    refetchQueries: [
      { query: GET_MESSAGES }, // atau GET_MESSAGES secara langsung jika sudah diimpor
      'GetMessages' // Nama operasi query juga bisa digunakan
    ],
    // Atau, Anda bisa menggunakan update cache secara manual jika lebih kompleks
    // update: (cache, { data: { addMessage: newMessage } }) => {
    //   const existingData = cache.readQuery({ query: GET_MESSAGES });
    //   if (existingData && existingData.getMessages) {
    //     cache.writeQuery({
    //       query: GET_MESSAGES,
    //       data: { getMessages: [...existingData.getMessages, newMessage] },
    //     });
    //   }
    // }
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!messageText.trim()) return;
    try {
      await addMessage({ variables: { text: messageText } });
      setMessageText(''); // Kosongkan input setelah berhasil
    } catch (err) {
      console.error("Error adding message:", err);
      // Penanganan error bisa ditampilkan ke pengguna di sini
    }
  };

  return (
    <div>
      <h2>Add New Message</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={messageText}
          onChange={(e) => setMessageText(e.target.value)}
          placeholder="Enter your message"
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Adding...' : 'Add Message'}
        </button>
      </form>
      {error && <p style={{ color: 'red' }}>Error adding message: {error.message}</p>}
      {data && <p style={{ color: 'green' }}>Message "{data.addMessage}" added!</p>}
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>GraphQL Client-Server App</h1>
      </header>
      <main>
        <MessagesList />
        <hr />
        <AddMessageForm />
      </main>
    </div>
  );
}

export default App;
